import styled from "styled-components";

export const Wrapper = styled.div`
  padding: 8px;
  border-radius: 8px;
  border: 1px solid #ccc;
  margin: 16px 16px;
  width: 350px;
  height: 150px;
  align-items: center;
  align-content: center;
  justify-content: center;
  justify-items: center;
  display: flex;
  flex-direction: column;
  button {
    background-color: #f85313;
    box-shadow: 3px 2px 10px rgba(0, 0, 0, 0.5);
    padding: 8px 16px;
    margin: 0 16px;
    border-radius: 8px;

    &:hover {
      box-shadow: 3px 2px 10px rgba(0, 0, 0, 1);
    }
`;

export const WrapperTitle = styled.h2`
  font-size: 18px;
  font-weight: bold;
  margin: 8px 0;
  color: black
`;

export const WrapperFullName = styled.h2`
  font-size: 16px;
  font-weight: bold;
  margin: 8px 0;

`;

export const WrapperLink = styled.a`
  font-size: 16px;
  font-weight: bold;
  margin: 8px 0;

`;
